import React, { Component } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import HomeIcon from "@material-ui/icons/Home";
import { TextField } from "@material-ui/core";
import imgDatos from "../imgDatos.png";

//import { Button } from "@material-ui/core";
import Dropdown from "react-dropdown";
import "react-dropdown/style.css";

import {
  Button,
  Container,
  List,
  ListItem,
  ListSubheader,
  ListItemText,
  ListItemIcon,
} from "@material-ui/core";

import EditIcon from "@material-ui/icons/Edit";
import DeleteIcon from "@material-ui/icons/Delete";
import PersonIcon from "@material-ui/icons/Person";

class EvaluacionView extends Component {
  titulo = "EVALUACIÓN DE MIS PACIENTES:";

  frmEstatura = React.createRef();
  frmPeso = React.createRef();
  frmEdad = React.createRef();
  frmPorcentajeGrasa = React.createRef();
  frmComplexion = React.createRef();
  frmFechaNacimiento = React.createRef();

  constructor(props) {
    super(props);
    this.state = {
      value: "Paciente1",
      edit: false,
      idPaciente: 0,
      pacientes: [],
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({ value: event.target.value });
  }

  handleSubmit(event) {
    alert("Your favorite flavor is: " + this.state.value);
    event.preventDefault();
  }

  addPaciente = event => {
    event.preventDefault();

    //const url = 'http://localhost:5000/api/pacientes';

    console.log("Sí entra");

    const data = {
      nombre: this.frmNombre.value,
      aPaterno: this.frmApaterno.value,
      aMaterno: this.frmAmaterno.value,
      email: this.frmEmail.value,
      telefono: this.frmTelefono.value,
      fechaNacimiento: this.frmFechaNacimiento.value,
    }

    if (!this.state.edit) {
      const url = "http://localhost:5000/api/pacientes/";
      axios.post(url, data).then(res => console.log(res.data));
      this.frmNombre.value = "";
      this.frmApaterno.value = "";
      this.frmAmaterno.value = "";
      this.frmEmail.value = "";
      this.frmTelefono.value = "";
      this.frmFechaNacimiento.value = "";

      this.frmNombre.focus();
      this.frmApaterno.focus();
      this.frmAmaterno.focus();
      this.frmEmail.focus();
      this.frmTelefono.focus();
      this.frmFechaNacimiento.focus();
    } else {
      const url = "http://localhost:5000/api/pacientes/" + this.state.id;
      const data = {
        nombre: this.frmNombre.value,
        aPaterno: this.frmApaterno.value,
        aMaterno: this.frmAmaterno.value,
        email: this.frmEmail.value,
        telefono: this.frmTelefono.value,
        fechaNacimiento: this.frmFechaNacimiento.value,
      }
      axios.put(url, data).then(res => console.log(res.data));
    }
    this.loadPaciente();
  }

  viewPaciente = (id) => event => {
    event.preventDefault();

    this.frmNombre.value = "";
    this.frmApaterno.value = "";
    this.frmAmaterno.value = "";
    this.frmEmail.value = "";
    this.frmTelefono.value = "";
    this.frmFechaNacimiento.value = "";
    this.frmNombre.focus();
    this.frmApaterno.focus();
    this.frmAmaterno.focus();
    this.frmEmail.focus();
    this.frmTelefono.focus();
    this.frmFechaNacimiento.focus();
    this.frmNombre.value = this.state.pacientes[id];
    this.frmApaterno.value = this.state.pacientes[id];
    this.frmAmaterno.value = this.state.pacientes[id];
    this.frmEmail.value = this.state.pacientes[id];
    this.frmTelefono.value = this.state.pacientes[id];
    this.frmFechaNacimiento.value = this.state.pacientes[id];
  }

  editPaciente = (id, row) => event => {
    event.preventDefault();
    var newState = this.state;
    newState.edit = true;
    newState.id = id;
    this.setState(newState);

    this.frmNombre.focus();
    this.frmNombre.value = "";
    this.frmNombre.value = this.state.pacientes[row].nombre;
    this.frmApaterno.focus();
    this.frmApaterno.value = "";
    this.frmApaterno.value = this.state.pacientes[row].aPaterno;
    this.frmAmaterno.focus();
    this.frmAmaterno.value = "";
    this.frmAmaterno.value = this.state.pacientes[row].aMaterno;
    this.frmEmail.focus();
    this.frmEmail.value = "";
    this.frmEmail.value = this.state.pacientes[row].email;
    this.frmTelefono.focus();
    this.frmTelefono.value = "";
    this.frmTelefono.value = this.state.pacientes[row].telefono;
    this.frmFechaNacimiento.focus();
    this.frmFechaNacimiento.value = "";
    this.frmFechaNacimiento.value = this.state.pacientes[row].fechaNacimiento;
    this.loadPaciente();
  }

  deletePaciente = (id) => event => {
    event.preventDefault();
    const url = "http://localhost:5000/api/pacientes/" + id;
    axios.delete(url).then(res => console.log(res.data));
    this.loadPaciente();
  }

  loadPaciente() {
    axios.get("http://localhost:5000/api/pacientes/").then(res => {
      this.setState({ pacientes: res.data });
    });
  }

  componentDidMount() {
    this.loadPaciente();
  }

  render() {
    return (
      <div className="css-evaluacion">
        <Link to="/">
          <Button
            variant="contained"
            color="default"
            size="small"
            startIcon={<HomeIcon />}
          >
            Regresar
          </Button>
        </Link>
        <h1>{this.props.name}</h1>
        <form autoComplete="off" onSubmit={this.addData}>
          <table>
            <tr>
              <td>
                <tr>
                  <TextField
                    label="Estatura(cm)"
                    type="numeric"
                    keyboardType="numeric"
                    //onChange={(e) => this.onChange(e)}
                    margin="normal"
                    variant="outlined"
                    inputRef={(e) => (this.frmEstatura = e)}
                  />
                </tr>
                <tr>
                  <TextField
                    label="Peso(gr)"
                    type="numeric"
                    keyboardType="numeric"
                    //onChange={(e) => this.onCambios(e)}
                    margin="normal"
                    variant="outlined"
                    inputRef={(e) => (this.frmPeso = e)}
                  />
                </tr>
                <tr></tr>
                <tr>
                  <TextField
                    label="Edad"
                    keyboardType="numeric"
                    //onChange={(e) => this.onChange(e)}
                    //input
                    //value={this.state.value}
                    margin="normal"
                    variant="outlined"
                    inputRef={(e) => (this.frmEdad = e)}
                  />
                </tr>
                <tr>
                  <TextField
                    label="Grasa(%)"
                    keyboardType="numeric"
                    //onChange={(e) => this.onChange(e)}
                    //input
                    //value={this.state.value}
                    margin="normal"
                    variant="outlined"
                    inputRef={(e) => (this.frmPorcentajeGrasa = e)}
                  />
                </tr>
                <tr>
                  <TextField
                    label="Complexión"
                    type="text"
                    margin="normal"
                    variant="outlined"
                    inputRef={(e) => (this.frmComplexion = e)}
                  />
                </tr>
              </td>
              &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
              <td>
                <tr>
                  <img
                    src={imgEvaluacion}
                    className="App-logo"
                    alt="imgEvaluacion"
                  />
                </tr>
              </td>
              &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
              <td>
                {/* <tr>
                  <div className={classes.root}>
                    <Typography id="discrete-slider-always" gutterBottom>
                      Always visible
                    </Typography>
                    <Slider
                      defaultValue={80}
                      getAriaValueText={valuetext}
                      aria-labelledby="discrete-slider-always"
                      step={10}
                      marks={marks}
                      valueLabelDisplay="on"
                    />
                  </div>
                </tr> */}
                <tr>
                <img
                    src={medidas}
                    className="App-logo"
                    alt="medidas"
                  />                    
                </tr>
              </td>
            </tr>
          </table>
        </form>
      </div>
    );
  }
}

export default EvaluacionView;
